import React, { useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Hero from "./components/Hero";
import About from './components/about';
import Skills from "./components/skills";
import Projects from "./components/projects";
import Contact from "./components/Contact";
import Footer from "./components/footer";
import ThankYou from "./components/Thankyou";
import Internships from "./components/internship";
import "./App.css";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);
export default function App() {
  const [isLightMode, setIsLightMode] = useState(false);

  const toggleTheme = () => {
    setIsLightMode(prev => !prev);
  };

  return (
    <div className={isLightMode ? "light-mode" : "dark-mode"}>
      <BrowserRouter>
        <Navbar toggleTheme={toggleTheme} isLightMode={isLightMode} />

        {/* All routed content should be within this */}
        <Routes>
          <Route path="/" element={<>
            <Hero />
            <About />
            <Skills />
            <Internships />
            <Projects />
            <Contact />
            <Footer />
          </>} />

          {/* If you still want individual route access */}
          <Route path="/about" element={<About />} />
          <Route path="/skills" element={<Skills />} />
          <Route path="/internship" element={<Internships />} />
          <Route path="/projects" element={<Projects />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/thankyou" element={<ThankYou />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}
