import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import Pro from "./assest/pro.png";
import "./Hero.css";
import Resume from "./resume.pdf";
import About from "./about";
import Skills from "./skills.jsx";
import Projects from "./projects.jsx";
import Contact from "./Contact.js";

export default function Hero({ toggleTheme, isLightMode }) {
  const nameChars = useRef([]);
  const roleChars = useRef([]);
  const buttonRef = useRef(null);
  const paraRef = useRef(null);
  const imgRef = useRef(null);

  const name = "Sakshi Kadu";
  const role = "Web Developer";

  useEffect(() => {
    // Animate name (character by character)
    gsap.from(nameChars.current, {
      y: 40,
      opacity: 0,
      stagger: 0.05,
      duration: 1,
      ease: "back.out(1.7)",
    });

    // Animate role (character by character)
    gsap.from(roleChars.current, {
      y: 20,
      opacity: 0,
      stagger: 0.05,
      delay: 0.5,
      duration: 1,
      ease: "power3.out",
    });

    // Animate paragraph
    gsap.from(paraRef.current, {
      opacity: 0,
      y: 20,
      delay: 1.2,
      duration: 1,
    });

    // Animate buttons
    gsap.from(buttonRef.current, {
      opacity: 0,
      y: 20,
      delay: 1.5,
      duration: 1,
      stagger: 0.2,
    });

    // Animate image
    gsap.from(imgRef.current, {
      scale: 0.8,
      opacity: 0,
      delay: 1,
      duration: 1,
      ease: "power2.out",
    });
  }, []);

  return (
    <>
      <section id="home" className="hero">
        <div className="left">
          <h1>
            Hello<span>.</span>
          </h1>
          <h4>
            I'm{" "}
            <span className="hero-name">
              {name.split("").map((char, i) => (
                <span
                  key={i}
                  className="char-span"
                  ref={(el) => (nameChars.current[i] = el)}
                >
                  {char}
                </span>
              ))}
            </span>
          </h4>
          <h3 className="hero-role">
            {role.split("").map((char, i) => (
              <span
                key={i}
                className="char-span"
                ref={(el) => (roleChars.current[i] = el)}
              >
                {char}
              </span>
            ))}
          </h3>

          <p ref={paraRef}>
            I'm a passionate Web Developer focused on building responsive and
            interactive websites. I love turning ideas into real-world web
            experiences using React, JavaScript, and creative design.
          </p>

          <div className="buttons" ref={buttonRef}>
            <a href="#about">
              <button className="Learn_more">Learn More</button>
            </a>
            <a href={Resume} download>
              <button className="resume">Download Resume</button>
            </a>
          </div>
        </div>

        <div className="right">
          <img src={Pro} alt="profile" ref={imgRef} />
        </div>
      </section>

      {/* Other sections */}
      <About />
      <Skills />
      <Projects />
      <Contact />
    </>
  );
}
