import React from "react";
import "./about.css";
import gsap from 'gsap';
import { useRef } from "react";
import { useGSAP } from '@gsap/react';

export default function About() {
   const textRef = useRef();

  useGSAP(() => {
    const letters = gsap.utils.toArray(".letter");

    gsap.to(letters, {
      scrollTrigger: {
        trigger: textRef.current,
        start: "top 80%",
      },
      opacity: 1,
      y: 0,
      duration: 0.4,
      stagger: 0.02,
      ease: "power3.out",
    });
  }, [textRef]);

  const paragraphText =
    "I'm a passionate Web Developer focused on building responsive and interactive websites. I love turning ideas into real-world web experiences using React, JavaScript, and creative design.";

  const h1ref = useRef(null);
  useGSAP(() => {
    gsap.from(h1ref.current, {
      duration: 0.6,
      opacity: 0,
      scale: 0.5,
      y: -20,
    });
  }, []);
  return (
    <section id="about" className="about-section">
      <h2>About Me</h2>
      <p>
        I'm <strong>Sakshi Kadu</strong>, A 2<sup>nd</sup> year Computer Science and Engineering
        student at YCCE, Nagpur, graduating in 2027. Iâ€™m passionate about
        crafting meaningful digital experiences through clean code and
        thoughtful design.
      </p>
      <p>
        My journey started with simple HTML pages and grew into full-fledged
        projects using JavaScript, React, Bootstrap, and GSAP. I enjoy bringing
        ideas to life through responsive and interactive interfaces. I love
        working with <span className="highlight">React</span>
      </p>
      <p>
        I actively explore Data Structures & Algorithms to strengthen my
        problem-solving skills. For me, code is not just syntax â€” itâ€™s a tool to
        think, debug, and innovate.
      </p>
      <p>
        I'm constantly learning, improving, and building â€” not to know
        everything, but to keep growing every day.
      </p>

      {/* Terminal-style code block */}
      <div className="code-box">
        <p>&gt; sakshi.kadu@developer ~</p>
        <p> Passion: Web Development ðŸš€</p>
        <p> Skills: React, JavaScript, CSS, Git</p>
        <p> Learning: GSAP, DSA, Backend Basics</p>
      </div>
    </section>
  );
}
