import Pro from "./assest/pro.png";
import "./Hero.css";
import Resume from "./resume.pdf";
import About from "./about";
import Skills from "./skills.jsx";
import Projects from "./projects.jsx";
import Contact from "./Contact.js";
import React from "react";
import gsap from 'gsap';
import { useEffect, useRef } from "react";


export default function Hero({ toggleTheme, isLightMode }) {
  const h1Ref = useRef(null);
  const h2Ref = useRef(null);
  const h3Ref = useRef(null);

  useEffect(() => {
    gsap.from(h1Ref.current, {
      duration: 0.5,
      opacity: 0,
      y: -30,
      scale: 0.8,
    });

    gsap.from(h2Ref.current, {
      duration: 0.5,
      opacity: 0,
      y: 20,
      delay: 0.2,
    });

    gsap.from(h3Ref.current, {
      duration: 0.5,
      opacity: 0,
      y: 20,
      delay: 0.4,
    });
  }, []);
  return (
    <>
      <section id="home" className="hero">
        <div className="left">
          <h1>
            Hello<span>.</span>
          </h1>
          <h4>
            I'm <span ref={h1Ref}>Sakshi Kadu</span>
          </h4>
          <h3>Web Developer</h3>

          <p>
            I'm a passionate Web Developer focused on building responsive and
            interactive websites. I love turning ideas into real-world web
            experiences using React, JavaScript, and creative design.
          </p>
          <div className="buttons">
            <a href="#about">
              <button className="Learn_more">Learn More</button>
            </a>
            <a href={Resume} download>
              <button className="resume">Download Resume</button>
            </a>
          </div>
        </div>
        <div className="right">
          <img src={Pro} alt="profile" />
        </div>
      </section>
      <About />
      <Skills />
      <Projects />
      <Contact />
    </>
  );
}
