import React, { useState } from 'react';
import './SocialBar.css';
import { FaLinkedin, FaGithub, FaAngleDown, FaAngleUp, FaBars } from 'react-icons/fa';

const SocialBar = ({ toggleTheme }) => {
  const [showBar, setShowBar] = useState(false);

  const toggleBar = () => {
    setShowBar(!showBar);
  };

  return (
    <div className="social-wrapper">
      <button className="toggle-icon" onClick={toggleBar}>
        {showBar ? <FaAngleUp /> : <FaBars />}
      </button>

      {showBar && (
        <div className="social-bar">
          <a href="https://linkedin.com/in/sakshi-kadu-40a441322" target="_blank">
            <FaLinkedin />
          </a>
          <a href="https://github.com/sakshi123509" target="_blank">
            <FaGithub />
          </a>
          <button onClick={toggleTheme} className="theme-btn">ðŸŒ™</button>
        </div>
      )}
    </div>
  );
};

export default SocialBar;
