// Internship.jsx
import React from 'react';
import codsoftLogo from './assest/codsoft.jpg'; // update path as needed
import deloitteLogo from './assest/deolite.png'; 
import './internship.css'; 

const Internship = () => {
  return (
    <section id="internship" className="py-16 bg-gradient-to-r from-[#1f005c] to-[#0f172a] text-white">
      <h2 className="text-4xl font-bold text-center text-purple-300 mb-10">Internships</h2>

      <div className="max-w-5xl mx-auto space-y-10 px-4">

        {/* CodSoft Card */}
        <div className="flex flex-col md:flex-row items-center justify-between bg-[#1e293b] p-6 rounded-xl shadow-lg hover:scale-[1.02] transition-all">
          <div className="text-left md:w-3/4 space-y-2">
            <h3 className="text-2xl font-semibold text-white">Web Developer Intern @ CodSoft</h3>
            <p className="text-sm text-gray-400">June 2025 â€“ July 2025</p>
            <ul className="list-disc list-inside text-gray-300 text-sm">
              <li>Built responsive UIs using React and CSS</li>
              <li>Collaborated using Git & GitHub</li>
              <li>Focused on frontend skills with real projects</li>
            </ul>
          </div>
          <img src={codsoftLogo} alt="CodSoft Logo" className="w-24 h-auto mt-4 md:mt-0" />
        </div>

        {/* Deloitte Card */}
        <div className="flex flex-col md:flex-row-reverse items-center justify-between bg-[#1e293b] p-6 rounded-xl shadow-lg hover:scale-[1.02] transition-all">
          <div className="text-left md:w-3/4 space-y-2">
            <h3 className="text-2xl font-semibold text-white">Virtual Internship @ Deloitte (Forage)</h3>
            <p className="text-sm text-gray-400">May 2025</p>
            <ul className="list-disc list-inside text-gray-300 text-sm">
              <li>Proposed real-world dashboard solutions</li>
              <li>Applied design thinking for client simulation</li>
              <li>Completed hands-on, task-based challenges</li>
            </ul>
          </div>
          <img src={deloitteLogo} alt="Deloitte Logo" className="w-24 h-auto mt-4 md:mt-0" />
        </div>

      </div>
    </section>
  );
};

export default Internship;
