import React, { useState, useEffect, useRef } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import gsap from "gsap";
import Navbar from "./components/Navbar";
import SocialBar from "./components/social_bar";
import Hero from "./components/Hero";
import About from './components/about';
import Skills from "./components/skills";
import Projects from "./components/projects";
import Contact from "./components/Contact";
import Footer from "./components/footer";
import "./App.css";

export default function App() {
  const [isLightMode, setIsLightMode] = useState(false); // dark mode default

  const toggleTheme = () => {
    setIsLightMode(prev => !prev);
  };
  const appRef = useRef(null);

  useEffect(() => {
    gsap.from(appRef.current, {
      opacity: 0,
      y: 30,
      duration: 1.5,
      ease: "power2.out",
    });
  }, []);
  return (
    <>
      <div className={isLightMode ? "light-mode" : "dark-mode"}>
        <BrowserRouter>
          <Navbar toggleTheme={toggleTheme} isLightMode={isLightMode} />
          <SocialBar />
          <Routes>
            <Route path="/" element={<Hero />} />
            <Route path="/about" element={<About />} />
            <Route path="/skills" element={<Skills />} />
            <Route path="/Projects" element={<Projects />} />
            <Route path="/Contact" element={<Contact />} />
          </Routes>
          <Footer />
        </BrowserRouter>
      </div >

    </>
  );
}
