import Pro from "./assest/pro.png";
import "./Hero.css";
import Resume from "./resume.pdf";
import About from "./about";
import Skills from "./skills.jsx";
import Projects from "./projects.jsx";
import Contact from "./Contact.js";
// import React from "react";
// import gsap from 'gsap';
// import { useEffect, useRef } from "react";
import Internship from "./internship.jsx";


export default function Hero({ toggleTheme, isLightMode }) {

  return (
    <>
      <section id="home" className="hero">
        <div className="left">
          <h1>
            Hello<span>.</span>
          </h1>
          <h4>
            I'm <span>Sakshi Kadu</span>
          </h4>
          <h3>Web Developer</h3>

          <p>
            I'm a passionate Web Developer focused on building responsive and
            interactive websites. I love turning ideas into real-world web
            experiences using React, JavaScript, and creative design.
          </p>
          <div className="buttons">
            <a href="#about">
              <button className="Learn_more">Learn More</button>
            </a>
            <a href={Resume} download>
              <button className="resume">Download Resume</button>
            </a>
          </div>
        </div>
        <div className="right">
          <img src={Pro} alt="profile" />
        </div>
      </section>
      <About />
      <Skills />
      <Internship />
      <Projects />
      <Contact />
    </>
  );
}
