import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import SplitText from "./gsap/SplitText.min"; // Make sure this path is correct
import Pro from "./assest/pro.png";
import "./Hero.css";
import Resume from "./resume.pdf";
import About from "./about";
import Skills from "./skills.jsx";
import Projects from "./projects.jsx";
import Contact from "./Contact.js";

gsap.registerPlugin(SplitText);

export default function Hero({ toggleTheme, isLightMode }) {
  const roleRef = useRef(null);
  const paraRef = useRef(null);
  const buttonRef = useRef(null);
  const imgRef = useRef(null);

  useEffect(() => {
    // Animate "Sakshi Kadu" using SplitText
    const split = new SplitText(".hero-name", { type: "chars" });
    gsap.from(split.chars, {
      y: 40,
      opacity: 0,
      stagger: 0.05,
      ease: "power2.out",
      duration: 1.2,
    });

    // Animate role
    gsap.from(roleRef.current, {
      x: 80,
      opacity: 0,
      delay: 1,
      duration: 1,
      ease: "power3.out",
    });

    // Animate paragraph
    gsap.from(paraRef.current, {
      opacity: 0,
      y: 20,
      delay: 1.5,
      duration: 1,
    });

    // Animate buttons
    gsap.from(buttonRef.current.children, {
      opacity: 0,
      y: 30,
      delay: 2,
      duration: 1,
      stagger: 0.2,
      ease: "back.out(1.7)",
    });

    // Animate profile image
    gsap.from(imgRef.current, {
      scale: 0.85,
      opacity: 0,
      delay: 1.5,
      duration: 1.2,
      ease: "power2.out",
    });
  }, []);

  return (
    <>
      <section id="home" className="hero">
        <div className="left">
          <h1>
            Hello<span>.</span>
          </h1>
          <h4>
            I'm <span className="hero-name">Sakshi Kadu</span>
          </h4>
          <h3 ref={roleRef}>Web Developer</h3>

          <p ref={paraRef}>
            I'm a passionate Web Developer focused on building responsive and
            interactive websites. I love turning ideas into real-world web
            experiences using React, JavaScript, and creative design.
          </p>

          <div className="buttons" ref={buttonRef}>
            <a href="#about">
              <button className="Learn_more">Learn More</button>
            </a>
            <a href={Resume} download>
              <button className="resume">Download Resume</button>
            </a>
          </div>
        </div>

        <div className="right">
          <img src={Pro} alt="profile" ref={imgRef} />
        </div>
      </section>

      <About />
      <Skills />
      <Projects />
      <Contact />
    </>
  );
}
