import React from "react";
import "./projects.css"; // You'll create this
// import bmiImg from "./assest/";
import Tic_Tac_toe from './assest/tic tac toe.png'
import calcImg from "./assests/"
import amazonImg from "../assets/amazon.png";
import guessImg from "../assets/guess.png";
import landingImg from "../assets/landing.png";

const projects = [
  {
    title: "BMI Generator",
    img: bmiImg,
    description: "Simple BMI calculator built using HTML, CSS, and JavaScript. It helps users find their BMI quickly.",
    github: "https://github.com/yourusername/bmi-generator",
    live: "https://yourusername.github.io/bmi-generator",
  },
  {
    title: "Basic Calculator",
    img: calcImg,
    description: "Fully functional calculator to perform basic arithmetic operations. Built with JS logic and clean UI.",
    github: "https://github.com/yourusername/calculator",
    live: "https://yourusername.github.io/calculator",
  },
  {
    title: "Amazon UI Clone",
    img: amazonImg,
    description: "Amazon homepage clone using HTML/CSS. Focused on layout, responsiveness, and component structure.",
    github: "https://github.com/yourusername/amazon-ui-clone",
    live: "https://yourusername.github.io/amazon-ui-clone",
  },
  {
    title: "Guess The Number",
    img: guessImg,
    description: "Fun number guessing game with JavaScript logic, DOM handling and alert-based feedback.",
    github: "https://github.com/yourusername/guess-the-number",
    live: "https://yourusername.github.io/guess-the-number",
  },
  {
    title: "Landing Page",
    img: landingImg,
    description: "Creative and animated landing page design — built using HTML, CSS, and basic animation concepts.",
    github: "https://github.com/yourusername/landing-page",
    live: "https://yourusername.github.io/landing-page",
  },
   {
    title: "Tic Tac Toe",
    img: Tic_Tac_toe,
    description: "Creative and animated landing page design — built using HTML, CSS, and basic animation concepts.",
    github: "https://github.com/yourusername/landing-page",
    live: "https://yourusername.github.io/landing-page",
  },
];

export default function Projects() {
  return (
    <section className="projects-section" id="projects">
      <h2>Projects</h2>
      <div className="project-grid">
        {projects.map((project, index) => (
          <div className="project-card" key={index}>
            <img src={project.img} alt={project.title} />
            <h3>{project.title}</h3>
            <p>{project.description}</p>
            <div className="project-links">
              <a href={project.github} target="_blank" rel="noopener noreferrer">GitHub</a>
              <a href={project.live} target="_blank" rel="noopener noreferrer">Live Preview</a>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
}
