import Pro from "./assest/pro.png";
import "./Hero.css";
import Resume from "./resume.pdf";
import About from "./about";
import Skills from "./skills.jsx";
import Projects from "./projects.jsx";
import Contact from "./Contact.js";
import React, { useRef } from "react";
import gsap from 'gsap';
import { useGSAP } from '@gsap/react';
import Internship from "./internship.jsx";
import { ScrollTrigger } from "gsap/ScrollTrigger";

gsap.registerPlugin(ScrollTrigger);

export default function Hero({ toggleTheme, isLightMode }) {
  const h1ref = useRef(null);
  const textRef = useRef(null);

  // Animation for heading
  useGSAP(() => {
    gsap.from(h1ref.current, {
      duration: 0.6,
      opacity: 0,
      scale: 0.5,
      y: -20,
    });
  }, []);

  // Animation for each word in the paragraph
  useGSAP(() => {
    const words = gsap.utils.toArray(".word");

    gsap.from(words, {
      scrollTrigger: {
        trigger: textRef.current,
        start: "top 80%",
      },
      opacity: 0,
      y: 20,
      stagger: 0.15,
      duration: 0.8,
      ease: "power3.out"
    });
  }, []);

  const paragraphText = "I'm a passionate Web Developer focused on building responsive and interactive websites. I love turning ideas into real-world web experiences using React, JavaScript, and creative design.";

  return (
    <>
      <section id="home" className="hero">
        <div className="left">
          <h1>
            Hello<span>.</span>
          </h1>
          <h4>
            I'm <h3 className="heading" ref={h1ref}>Sakshi Kadu</h3>
          </h4>
          <h3>Web Developer</h3>

          {/* âœ… Animated Paragraph */}
          <p ref={textRef}>
            {paragraphText.split(" ").map((word, idx) => (
              <span key={idx} className="word" style={{ marginRight: "8px", display: "inline-block" }}>
                {word}
              </span>
            ))}
          </p>

          <div className="buttons">
            <a href="#about">
              <button className="Learn_more">Learn More</button>
            </a>
            <a href={Resume} download>
              <button className="resume">Download Resume</button>
            </a>
          </div>
        </div>

        <div className="right">
          <img src={Pro} alt="profile" />
        </div>
      </section>

      {/* Subsections */}
      <About />
      <Skills />
      <Internship />
      <Projects />
      <Contact />
    </>
  );
}
