import { useEffect } from "react";
import { gsap } from "gsap";
import ".internship.css";

const Internships = () => {
  useEffect(() => {
    gsap.from(".internship-card", {
      opacity: 0,
      y: 50,
      duration: 0.6,
      stagger: 0.3,
    });
  }, []);

  return (
    <section id="internships" className="bg-[#0f172a] text-white py-12 px-8">
      <h2 className="text-3xl font-bold mb-6 text-center text-purple-300">
        Internships
      </h2>

      <div className="max-w-4xl mx-auto space-y-6">
        <div className="bg-[#1e293b] p-6 rounded-xl shadow-md hover:scale-[1.02] transition">
          <h3 className="text-xl font-semibold text-white">
            Web Developer Intern @ CodSoft
          </h3>
          <p className="text-sm text-gray-300 mb-2">June 2025 â€“ July 2025</p>
          <ul className="list-disc list-inside text-gray-200">
            <li>Built responsive UIs using React.js and CSS</li>
            <li>Used Git & GitHub for version control</li>
            <li>Worked with mentors to improve frontend skills</li>
          </ul>
        </div>

        <div className="bg-[#1e293b] p-6 rounded-xl shadow-md hover:scale-[1.02] transition">
          <h3 className="text-xl font-semibold text-white">
            Deloitte Australia Technology Virtual Internship
          </h3>
          <p className="text-sm text-gray-300 mb-2">May 2025 (via Forage)</p>
          <ul className="list-disc list-inside text-gray-200">
            <li>Explored real-world tech scenarios and client dashboards</li>
            <li>Proposed innovative tech solutions</li>
            <li>Worked on UI-based dashboards using design-thinking</li>
          </ul>
        </div>
      </div>
    </section>
  );
};

export default Internships;
