import React, { useRef, useState } from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import SocialBar from "./components/social_bar";
import Hero from "./components/Hero";
import About from './components/about';
import Skills from "./components/skills";
import Projects from "./components/projects";
import Contact from "./components/Contact";
import Footer from "./components/footer";
import ThankYou from "./components/Thankyou";
import Internships from "./components/internship";
import "./App.css";
import gsap from 'gsap';
import { useGSAP } from '@gsap/react';

export default function App() {
  const [isLightMode, setIsLightMode] = useState(false); // dark mode default
  const h1ref = useRef(null);

  // ✅ GSAP Animation
  useGSAP(() => {
    gsap.from(h1ref.current, {
      duration: 0.6,
      opacity: 0,
      scale: 0.5,
      y: -20,
    });
  }, []);

  // ✅ Toggle Theme Function
  const toggleTheme = () => {
    setIsLightMode(prev => !prev);
  };

  return (
    <div className={isLightMode ? "light-mode" : "dark-mode"}>
      <BrowserRouter>
        <Navbar toggleTheme={toggleTheme} isLightMode={isLightMode} />
        <SocialBar />
        <h1 ref={h1ref} style={{ textAlign: "center"  }}>Welcome to My Portfolio</h1> {/* Example animated text */}

        <Routes>
          <Route path="/" element={<Hero />} />
          <Route path="/about" element={<About />} />
          <Route path="/skills" element={<Skills />} />
          <Route path="/internship" element={<Internships />} />
          <Route path="/projects" element={<Projects />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/thankyou" element={<ThankYou />} />
        </Routes>

        <Footer />
      </BrowserRouter>
    </div>
  );
}
