import React, { useEffect, useRef } from "react";
import gsap from "gsap";
import { ScrollTrigger } from "gsap/ScrollTrigger";
import './skills.css';
import HTML from "./assest/html.png";
import CSS from "./assest/css.png";
import JS from "./assest/js.svg";
// ... other imports

gsap.registerPlugin(ScrollTrigger);

export default function Skills() {
  const skillSectionRef = useRef(null);

  useEffect(() => {
    gsap.from(".skill-item", {
      scrollTrigger: {
        trigger: skillSectionRef.current,
        start: "top 80%",
        toggleActions: "play none none reverse", // onEnter
      },
      opacity: 0,
      y: 50,
      duration: 1,
      stagger: 0.2,
      ease: "power2.out",
    });
  }, []);

  return (
    <section className="skills-section" id="skills" ref={skillSectionRef}>
      <h2>Skills</h2>

      <div className="skills-category">
        <h3>Frontend</h3>
        <div className="skills-grid">
          <div className="skill-item"><img src={HTML} alt="HTML" /><p>HTML</p></div>
          <div className="skill-item"><img src={CSS} alt="CSS" /><p>CSS</p></div>
          <div className="skill-item"><img src={JS} alt="JS" /><p>JavaScript</p></div>
        </div>
      </div>

      {/* More categories... */}
    </section>
  );
}
